CREATE DATABASE proyecto;

USE proyecto;

CREATE TABLE Usuarios (
    ID_Usuario INT NOT NULL UNIQUE AUTO_INCREMENT,
    Nombre VARCHAR(50) NOT NULL,
    Apellidos VARCHAR(50) NOT NULL,
    Direccion VARCHAR(100) NOT NULL,
    Email VARCHAR(100) NOT NULL,
    Telefono VARCHAR(20) NOT NULL,
    Usuario VARCHAR(50) NOT NULL UNIQUE,
    Contrasena VARCHAR(100) NOT NULL,
    Administrador BOOLEAN NOT NULL,
    PRIMARY KEY(ID_Usuario)
);

CREATE TABLE Productos (
    ID_Producto INT NOT NULL UNIQUE AUTO_INCREMENT,
    Nombre VARCHAR(50) NOT NULL,
    Marca VARCHAR(50) NOT NULL,
    Categoria VARCHAR(40) NOT NULL,
    Descripcion VARCHAR(500) NOT NULL,
    Precio DECIMAL(10,2) NOT NULL,
    Stock INT NOT NULL,
    Vendidos INT NOT NULL,
    Imagen VARCHAR(500) NOT NULL,
    PRIMARY KEY(ID_Producto)
);

CREATE TABLE Carrito (
    ID_Carrito INT NOT NULL UNIQUE AUTO_INCREMENT,
    ID_Usuario INT NOT NULL UNIQUE,
    Total DECIMAL(10,2) NOT NULL,
    PRIMARY KEY(ID_Carrito),
    FOREIGN KEY(ID_Usuario) REFERENCES Usuarios(ID_Usuario)
);

CREATE TABLE Carrito_Productos (
    ID_Carrito INT NOT NULL,
    ID_Producto INT NOT NULL,
    Cantidad INT NOT NULL,
    Subtotal DECIMAL(10,2) NOT NULL,
    PRIMARY KEY(ID_Carrito, ID_Producto),
    FOREIGN KEY(ID_Carrito) REFERENCES Carrito(ID_Carrito),
    FOREIGN KEY(ID_Producto) REFERENCES Productos(ID_Producto)
);

CREATE TABLE Factura (
    ID_Factura INT NOT NULL UNIQUE AUTO_INCREMENT,
    ID_Usuario INT NOT NULL, 
    Total DECIMAL(10,2) NOT NULL,
    Fecha DATE NOT NULL,
    PRIMARY KEY(ID_Factura),
    FOREIGN KEY(ID_Usuario) REFERENCES Usuarios(ID_Usuario)
);

CREATE TABLE Factura_Productos (
    ID_Factura INT NOT NULL,
    ID_Producto INT NOT NULL,
    Cantidad INT NOT NULL,
    Subtotal DECIMAL(10,2) NOT NULL,
    FOREIGN KEY(ID_Factura) REFERENCES Factura(ID_Factura),
    FOREIGN KEY(ID_Producto) REFERENCES Productos(ID_Producto)
);

